using IAM2IDAS.SML;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Xml.Serialization;

namespace IAM2IDAS
{
    /// <summary>
    /// Contains the value generated by the om:procedure referenced for the referenced
    /// om:observedProperty.
    /// </summary>
    /// <example>
    /// <om:result>
    ///     <swe:Quantity
    ///     definition="urn:x-­‐ogc:def:phenomenon:IDAS:1.0:temperature">
    ///         <swe:uom
    ///         xlink:href="urn:x-­‐ogc:def:uom:IDAS:1.0:celsius"/>
    ///     <swe:value>17.4</swe:value>
    ///     </swe:Quantity>
    /// </om:result>
    /// </example>
    /// 
    [XmlType("result", Namespace = "http://www.opengis.net/om/1.0")]
    public class Result
    {
        [XmlElement("Quantity", Namespace = "http://www.opengis.net/swe/1.0.1")]
        public Quantity quantity;
        [XmlElement("uom", Namespace = "http://www.opengis.net/swe/1.0.1")]
        public String uom;
        [XmlElement("value", Namespace = "http://www.opengis.net/swe/1.0.1")]
        public String value;
        [XmlElement("Text", Namespace = "http://www.opengis.net/swe/1.0.1")]
        public Text text;
    }
}
